import React, { Component } from 'react'
import axios from 'axios';
let total_pass = 0;
let score = 0;
let count=0;
let total_avg =0;
let count_avg =0;
let score_avg =0;
let sub_count =0;
let final_score=0;
export  class Academic extends Component {
    addRow=()=>{
        sub_count = sub_count+1;        
        var row = document.getElementById('teachingSkill');
        var tr = document.createElement('tr');
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control'); 
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'sub_name'+sub_count);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'class1'+sub_count);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'pass'+sub_count);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('id', 'avg'+sub_count);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'feedback');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);


        var row = document.getElementById('teachingSkill');
        var tr = document.createElement('tr');
        var td = document.createElement('td');
        var inpt = document.createElement('h6');
        inpt.innerHTML='Content Expertise';
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var h6 = document.createElement('span');
        var inpt = document.createElement('input');
        h6.innerHTML='No Material  ';
        inpt.setAttribute('type', 'radio');
        inpt.setAttribute('id', 'material'+count);
        td.appendChild(h6);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var h6 = document.createElement('span');
        var inpt = document.createElement('input');
        h6.innerHTML='Sufficient Material  ';
        inpt.setAttribute('type', 'radio');
        inpt.setAttribute('id', 'material'+count);
        td.appendChild(h6);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var h6 = document.createElement('span');
        var inpt = document.createElement('input');
        h6.innerHTML='Good  ';
        inpt.setAttribute('type', 'radio');
        inpt.setAttribute('id', 'material'+count);
        td.appendChild(h6);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var h6 = document.createElement('span');
        var inpt = document.createElement('input');
        h6.innerHTML='Execellent';
        inpt.setAttribute('type', 'radio');
        inpt.setAttribute('id', 'material'+count);
        td.appendChild(h6);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);


        //second row

        var row = document.getElementById('teachingSkill');
        var tr = document.createElement('tr');
        var td = document.createElement('td');
        var inpt = document.createElement('h6');
        inpt.innerHTML='Instructional Design Skil  ';
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var h6 = document.createElement('span');
        var inpt = document.createElement('input');
        h6.innerHTML='Macro Plan  ';
        inpt.setAttribute('type', 'radio');
        inpt.setAttribute('id', 'IDS'+count);
        td.appendChild(h6);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var h6 = document.createElement('span');
        var inpt = document.createElement('input');
        h6.innerHTML='Micro plan  ';
        inpt.setAttribute('type', 'radio');
        inpt.setAttribute('id', 'IDS'+count);
        td.appendChild(h6);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var h6 = document.createElement('span');
        var inpt = document.createElement('input');
        h6.innerHTML='Hours required  ';
        inpt.setAttribute('type', 'text');
        td.appendChild(h6);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

       //3rd row

       var row = document.getElementById('teachingSkill');
       var tr = document.createElement('tr');
       var td = document.createElement('td');
       var inpt = document.createElement('h6');
       inpt.innerHTML='Instructional Delivery Skill';
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

       var td = document.createElement('td');
       var h6 = document.createElement('span');
       var inpt = document.createElement('input');
       h6.innerHTML='Need More Improvement  ';
       inpt.setAttribute('type', 'radio');
       inpt.setAttribute('name', 'delivery'+count);
       td.appendChild(h6);
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

       var td = document.createElement('td');
       var h6 = document.createElement('span');
       var inpt = document.createElement('input');
       h6.innerHTML='Average  ';
       inpt.setAttribute('type', 'radio');
       inpt.setAttribute('name', 'delivery'+count);
       td.appendChild(h6);
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

       var td = document.createElement('td');
       var h6 = document.createElement('span');
       var inpt = document.createElement('input');
       h6.innerHTML='Good  ';
       inpt.setAttribute('type', 'radio');
       inpt.setAttribute('name', 'delivery'+count);
       td.appendChild(h6);
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

       var td = document.createElement('td');
       var h6 = document.createElement('span');
       var inpt = document.createElement('input');
       h6.innerHTML='Execellent';
       inpt.setAttribute('type', 'radio');
       inpt.setAttribute('name', 'delivery'+count);
       td.appendChild(h6);
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

       //4th row

       var row = document.getElementById('teachingSkill');
       var tr = document.createElement('tr');
       var td = document.createElement('td');
       var inpt = document.createElement('h6');
       inpt.innerHTML='Instructional Assessment Skill';
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

       var td = document.createElement('td');
       var h6 = document.createElement('span');
       var inpt = document.createElement('input');
       h6.innerHTML='No of Assignment  ';
       inpt.setAttribute('type', 'text');
       inpt.setAttribute('class', 'form-control');
       inpt.setAttribute('id', 'assignment'+count);
       
       td.appendChild(h6);
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

       var td = document.createElement('td');
       var h6 = document.createElement('span');
       var inpt = document.createElement('input');
       h6.innerHTML='No of Class test  ';
       inpt.setAttribute('type', 'text');
       inpt.setAttribute('class', 'form-control');
       inpt.setAttribute('type', 'radio');
       
       
       td.appendChild(h6);
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

       var td = document.createElement('td');
       var h6 = document.createElement('span');
       var inpt = document.createElement('input');
       h6.innerHTML='Student Completed Certifications  ';
       inpt.setAttribute('type', 'text');
       inpt.setAttribute('class', 'form-control');
       inpt.setAttribute('id', 'certificate'+count);
       td.appendChild(h6);
       td.appendChild(inpt);
       tr.appendChild(td);
       row.appendChild(tr);

      //5th row
      var row = document.getElementById('teachingSkill');
      var tr = document.createElement('tr');
      var td = document.createElement('td');
      var inpt = document.createElement('h6');
      inpt.innerHTML='Managerial Skill';
      td.appendChild(inpt);
      tr.appendChild(td);
      row.appendChild(tr);

      var td = document.createElement('td');
      var h6 = document.createElement('span');
      var inpt = document.createElement('input');
      h6.innerHTML='No of Hours Conducted ';
      inpt.setAttribute('type', 'text');
      inpt.setAttribute('id', 'hrs_conducted'+count);
      inpt.setAttribute('class', 'form-control');
      td.appendChild(h6);
      td.appendChild(inpt);
      tr.appendChild(td);
      row.appendChild(tr);

      var td = document.createElement('td');
      var inpt = document.createElement('button');
      inpt.setAttribute('type', 'button');
      inpt.setAttribute('class', 'btn btn-primary mt-3');
      inpt.setAttribute('id', 'remove'+count);
      inpt.innerHTML='Remove';

      td.appendChild(inpt);
      tr.appendChild(td);
      row.appendChild(tr);   
  }
  remove_sub(event){
    let action1 = event.getElementById("remove0");
  }
  getScore(event){
      let pass = document.getElementById("pass0");
      total_pass = pass.value;      
      count+=1;
      score = 0; score_avg =0;
      if (total_pass >= 95)
          score = 25;
      else if (total_pass < 95 && total_pass >= 90)
          score = 20;
      else if (total_pass < 90 && total_pass >= 85)
          score = 15;
      else if (total_pass < 85 && total_pass >= 80)
          score = 10;
     else if (total_pass < 80 && total_pass >= 75)
          score = 5;
    let avg = document.getElementById("avg"+count_avg);
    alert(avg.value)
    total_avg = avg.value;
    count_avg = count_avg+1;
    if (total_avg >= 75) 
      score_avg = 25;
    else if (total_avg < 75 && total_avg >= 72)
      score_avg = 20;
    else if (total_avg < 72 && total_avg >= 69)
      score_avg = 15;
    else if (total_avg < 69 && total_avg >= 66)
      score_avg = 10;
    else if (total_avg < 60 && total_avg >= 63)
      score_avg = 5;
    alert(score_avg);
    let ce1 = document.getElementById("ce");
    alert("value in ce"+ce.value);
    //if(ce.checked==true){
    //  alert(ce.value)

    //}
    final_score = score+score_avg+final_score;
    let p=document.getElementById("Score");
    alert("final score :" + final_score);
    p.textContent = final_score/(count);
  }
render() {
      return(
        <div id = "root" className ="container border border-2 p-2 mt-5" > 
            <div  className="row mb-3">    
                    <div className="justify-content-end p-3 d-flex">
                    <h3 className="p-1">Academic Score(Max : 50)</h3>
                    <div className="border rounded p-2" style={{ width: '105px', height: "40px" }}>
                        <p id="Score">
                            0
                        </p>
                    </div>
                </div> </div>
                <div  className="input-group">
                      <label  className="input-group-text" >Academic Qualification </label>
                    </div>
                    
                  <div class="row">
                    <div class="col-sm-2">
                       <input class="form-check-input" type="checkbox" id="Ph.D" value="Ph.D"/>
                       <label class="form-check-label" >Ph.D</label>  
                    </div>
                    <div class="col-sm-4">
                        <input class="form-check-input" type="checkbox" id="PG"  value="PG"/>
                        <label class="form-check-label">M.Sc / M.Com / MBA / MCA / MA</label>
                    </div>

                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="NET"  value="NET"/>
                        <label class="form-check-label">NET</label>
                    </div>
                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="SLET" name="severity[]" value="SLET"/>
                        <label class="form-check-label">SLET</label>
                    </div>
            </div>
            <div className="mt-2">
               <h4>Academic Achievement</h4>
            </div>
                <div className="row  ">
                    <div className="col-10  p-3">
                        <table className="table table-bordered  mt-4 table-striped" >
                            <thead >
                              <tr>
                                <th scope="col">S.no</th>
                                <th scope="col">Name of Subject</th>
                                <th scope="col">Class</th>
                                <th scope="col">pass %</th>
                                <th scope="col">Average %</th>
                                </tr>
                                    </thead>
                                      <tbody id="teachingSkill">
                                        <tr>
                                          <td scope="row"><input type="text" class="form-control" ></input></td>
                                          <td><input id = "sub_name0" type="text" class="form-control" ></input></td>
                                          <td><input id = "class10" type="text" class="form-control" ></input></td>
                                          <td><input id = "pass0" type="text" class="form-control" ></input></td>
                                          <td><input id = "avg0" type="text" class="form-control" ></input></td>
                                        </tr>
                                        <tr >
                                        <td><h6>Content Expertise</h6></td>
                                        <td >No Material <input type="radio" name = "ce"  id="ce"></input></td>
                                        <td style={{width:"280px"}}>Sufficient Material  <input type="radio" id="ce" name = "ce" value = "0.5"></input></td>
                                        <td>Good <input type="radio" id="ce" name = "ce"></input></td>
                                        <td style={{width:"130px"}}>Execllent <input type="radio" id="ce" name = "ce"></input></td>
                                    </tr>
                                    <tr>
                                        <td><h6>Instructional Design Skil</h6></td>
