                                        <td><h6>Instructional Delivery Skill</h6></td>
                                        <td style={{width:"370px"}}>Need More Improvement<input type="radio" name = "delivery"></input></td>
                                        <td>Average<input type="radio" name = "delivery"></input></td>
                                        <td>Good <input type="radio" name = "delivery"></input></td>
                                        <td>Execllent <input type="radio" name = "delivery"></input></td>
                                    </tr>
                                        <tr> 
