import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './style.css';
import React, {location} from 'react';
import axios from 'axios';
import {Pie} from "react-chartjs-2";
import {Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import faker from 'faker';
var institution = [];
var total_fac = [];
var total_task_assigned = [];
var total_task_completed = [];
var total_task_overdue =[];
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);
export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top' 
    },
    title: {
      display: true,
      text: 'Chart.js Bar Chart',
    },
  },
};

const labels = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];
export const data = {
  labels,
  datasets: [
    {
      label: 'Dataset 1',
      data: labels.map(() => faker.datatype.number({ min: 0, max: 1000 })),
      backgroundColor: 'rgba(255, 99, 132, 0.5)',
    },
    {
      label: 'Dataset 2',
      data: labels.map(() => faker.datatype.number({ min: 0, max: 1000 })),
      backgroundColor: 'rgba(53, 162, 235, 0.5)',
    },
  ],
};


export class Mrhome extends React.Component{
    componentDidMount(){
       this.getdata();
    }
    getdata(){
      axios.post("http://localhost:3001/forInstitution").then((response)=>{
          console.log(response.data);
          institution=response.data
        })    
        axios.post("http://localhost:3001/total_faculty").then((response)=>{
          console.log(response.data);
          total_fac=response.data
        })    
        axios.post("http://localhost:3001/task_assigned").then((response)=>{
          console.log(response.data);
          total_task_assigned=response.data
        })    
        axios.post("http://localhost:3001/task_completed").then((response)=>{
          console.log(response.data);
          total_task_completed=response.data;
        })    
        axios.post("http://localhost:3001/task_overdue").then((response)=>{
          console.log(response.data);
          total_task_overdue = response.data
        })    
    }
    render() {
      return(
        <div style = {{display: 'flex', height: '100%'}}>
            <div style={{ flexGrow: 1 }}>
            <div  className="row mb-3">
                  <div  className="col-md-6 mb-3">
                    <Pie data = {data}></Pie>
                  </div>
                  <div  className="col-md-6 mb-3">
                    <Pie></Pie>
                  </div>
            </div>
            <div  className="row mb-3">
                  <div  className="col-md-6 mb-3">
                    <Pie data = {data}></Pie>
                  </div>
                  <div  className="col-md-6 mb-3">
                    <Pie data = {data}></Pie>
                  </div>
            </div>  
              </div>
            </div>
      );
    };
}
