import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './style.css';
import {DataGrid } from '@material-ui/data-grid'
import React from 'react';
import axios from 'axios';
import {useLocation, Link} from 'react-router-dom';
import { useParams } from 'react-router-dom';

export class Insthome extends React.Component { 
  state = {  
    dept_report:[]
  }
  componentDidMount(){
  
    this.getdata();
  }
  getdata=()=>
    {
      var inst_id = 1;
      axios.post("http://localhost:3001/dept_report",{inst_id:inst_id}).then((response)=>{
        console.log(response.data)
        this.setState({dept_report:response.data});
      }) 
    }
    render() 
    {
      const t_data = this.state.dept_report;        
      console.log(t_data);
      const task_acc_columns = [
        {
          headerName : 'ID',
          field  :'dept_id',
          width: 95
        },
        {
          headerName : 'Department Name',
          field  :"dept_name",
          width: 250 ,
          renderCell: (cellValues) => {
            return (
              <Link to = "/Facultyhome">{cellValues.value}</Link>
            );
          }
        },
        {
            headerName : "Members",
            field:"faculty_count",
            width:150
            
        },
        {
            headerName : "Task Assigned",
            field:"task_assigned",
            width:150,
            renderCell: (cellValues) => {
                return (
                  <Link to = "/dept_task_assigned">{cellValues.value}</Link>
                );
              }
        },
        {
            headerName : "Task Completed",
            field:"task_completed",
            width:150,
            renderCell: (cellValues) => {
                return (
                  <Link to = "/dept_task_completed">{cellValues.value}</Link>
                );
              }
        },
        {
            headerName : "Task OverDue",
            field:"task_overdue",
            width:150,
            renderCell: (cellValues) => {
                return (
                  <Link to = "/dept_task_overdue">{cellValues.value}</Link>
                );
              }
        }  
      ];
    return(
        <div style={{ display: 'flex', height: '100%'}}>
            <div style={{ flexGrow: 1 }}>
                <DataGrid id = "task_display" 
                    getRowId={rows => rows.dept_id}
                    rows = {t_data}
                    columns = {task_acc_columns}
                  />
            </div>
      </div>

    )
  }
}
