import React, { Component } from 'react'
import PropTypes from 'prop-types'
import axios from 'axios';
import {DataGrid } from '@material-ui/data-grid'

export class Academic extends Component {
    get_sub_details=()=>{        
        let con1 = document.createElement("div");
        let l1 = document.createElement("label");
        let sub_name = document.createElement("textbox");
        let sub_class = document.createElement("textbox");
        let pass_percentage = document.createElement("textbox");
        let sub_avg = document.createElement("textbox");
        con1.appendChild(l1);
        con1.appendChild(sub_name);
        con1.appendChild(sub_class);
        con1.appendChild(pass_percentage);
    }
    render(){
        const t_data = this.state.task_acc;        
        const task_acc_columns = [
          {
            headerName : 'S.No',
            field  :'id'
            /*width: 100*/
          },
          {
            headerName : 'Subject Name',
            field  :"Sub_name",
            resizable:true,
            /*width: 230,*/
            
          },
          {
            headerName : 'Class',
            field :'class',
            
            /*width: 160*/
          },
          {
            headerName : 'Number of Students ',
            field :'no.of.stu'
            /*width: 150*/
          },
          {
            headerName : 'Passed ',
            field :'passed'
            /*width: 150*/
          },
          {
            headerName : 'PASS %',
            field :'pass_percentage',
            /*width: 150*/
          },
          {
            headerName : 'Subject Average',
            field  :"Sub_Avg",
            width: 170,
          } 
         ]; 
  
      return(
        <div id = "root" className ="container border border-2 p-2 mt-5" > 
            <div  className="row mb-3">    
                    <div  className="input-group">
                      <label  className="input-group-text" >Academic Qualification </label>
                    </div>
                </div>
                  <div class="row">
                    <div class="col-sm-2">
                       <input class="form-check-input" type="checkbox" id="Ph.D" value="Ph.D"/>
                       <label class="form-check-label" >Ph.D</label>  
                    </div>
                    <div class="col-sm-4">
                        <input class="form-check-input" type="checkbox" id="PG"  value="PG"/>
                        <label class="form-check-label">M.Sc / M.Com / MBA / MCA / MA</label>
                    </div>

                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="NET"  value="NET"/>
                        <label class="form-check-label">NET</label>
                    </div>
                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="SLET" name="severity[]" value="SLET"/>
                        <label class="form-check-label">SLET</label>
                    </div>
            </div>
            <div  className="row mb-4">   
                <div class="col-md-8 mb-3"> 
                    <div  className="input-group">
                      <label  className="input-group-text" >Number of Subjects Handled </label>
                    </div>
                </div>
                <div class="col-md-7 mb-3"> 
                    <div className="input-group">
                    <input  className="form-control" type="text" name="Number of Subjects Handled" placeholder="Number of Subjects handled (including Practicals)" onChange={this.get_sub_details}/>
                    </div>
                </div>
                </div>

                <div style = {{display: 'flex', height: '100%'}}>
                  <div style={{ flexGrow: 1 }}>
                    <DataGrid id = "subject" 
                        getRowId={rows => rows.pr_id}
                        rows = {t_data}
                        columns = {task_acc_columns}
                        />
                    </div>
                    </div>
                </div>
        )}}
