import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './style.css';
import {DataGrid } from '@material-ui/data-grid'
import React, {location} from 'react';
import axios from 'axios';
export class Mrhome extends React.Component {  
   state = {
    temp_record : [],
    task_acc:[],
    total_task_assigned:[],
    total_task_completed:[],
    dataset : []
          /*{'Inst_id':0,
          'Inst_name':"",
          'count_faculty':0,
          'task_assign':"",
          'task_overdue':""}
      ] ;*/
    }
  componentDidMount()
  {
    this.getdata();
  }
  getdata=()=>
    {      
        axios.post('http://localhost:3001/total_faculty').then((response)=>{
               console.log(response.data);
                this.setState({task_acc:response.data});
                console.log(this.state.task_acc)
        });       
        axios.post("http://localhost:3001/total_task_assigned").then((response)=>{
          this.setState({total_task_assigned:response.data});
          console.log(this.state.total_task_assigned);       
        });     
        axios.post("http://localhost:3001/total_task_completed").then((response)=>{
          //console.log(response.data);
          this.setState({total_task_completed:response.data});
          console.log(this.state.total_task_completed);       
        }); 
        axios.post("http://localhost:3001/total_task_overdue").then((response)=>{
          //console.log(response.data);
          this.setState({total_task_overdue : response.data});
          console.log(this.state.total_task_overdue);
        });
        
        for(let i in this.state.task_acc)
        
          this.setState({temp_record:this.state.task_acc[i].inst_id});
          console.log(this.temp_record); 

      /*for (let i = 0; i<=this.state.total_task_assigned.length;i++){
            this.temp_record.Inst_id = this.state.total_task_assigned[i].inst_id;
            this.temp_record.Inst_name = this.state.total_task_assigned[i].inst_name;
            this.temp_record.count_faculty = this.state.total_task_assigned[i][2];
            for(let j = 0; j<=this.state.total_task_completed.length;j++){
                if(this.state.total_task_assigned[i].inst_id ==this.state.total_task_completed[j].inst_id)
                this.temp_record.task_assign =  this.state.total_task_completed[j][2]
            }
            for(let j = 0; j<=this.state.total_task_overdue.length;j++){
                if(this.state.total_task_assigned[i].inst_id ==this.state.total_task_completed[j].inst_id)
                this.temp_record.task_overdue =  this.state.total_task_completed[j][2]
            }
            console.log(this.temp_record)
        }  
        console.log("Array construction")
        //this.state.dataset.push(this.state.temp_record);
        console.log(this.state.dataset);*/
    }
    render() 
    {
      const t_data = this.state.dataset;        
      console.log(t_data);
      const task_acc_columns = [
        {
          headerName : 'ID',
          field  :'inst_id'
          /*width: 100*/
        },
        {
          headerName : 'Task Name',
          field  :"institution",
          width: 250 
        },
        {
            headerName : "Members",
            field:"count(empid)",
            width:150
        },
        {
            headerName : "Task Assigned",
            field:"",
            width:150
        },
        {
            headerName : "Task Completed",
            field:"",
            width:150
        }  ];

    return(
        <div style={{ display: 'flex', height: '100%' }}>
          <h1>report</h1>
            <div style={{ flexGrow: 1 }}>
                <DataGrid id = "task_display" 
                    getRowId={rows => rows.inst_id}
                    rows = {t_data}
                    columns = {task_acc_columns}
                />
            </div>
      </div>
    )
  }
}
