import React, { Component } from 'react'
import axios from 'axios';
let total_pass = 0;
let score = 0;
let avg_pass =0;
let count=0;
let total_avg =0;
let count_avg =0;
let avg_avg=0;
let score_avg =0;
let sub_count =0;
export  class Academic extends Component {
    addRow=()=>{
        
        var row = document.getElementById('tablecol');
        var tr = document.createElement('tr');
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');    
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'sub_name'+sub_count);
        alert(inpt.getAttribute("id"));
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'class1'+this.state.sub_count);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'pass'+this.state.sub_count);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('id', 'avg'+this.state.sub_count);
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);       
  }
  getScore(event){
      count++;
      alert("pass"+count);
      let pass = document.getElementById("pass"+count);
      alert(pass.id);
      total_pass = total_pass + pass.value;      
      avg_pass = total_pass / count;
      if (avg_pass >= 95) 
          score += 25;
      else if (avg_pass < 95 && avg_pass >= 90)
          score += 20;
      else if (avg_pass < 90 && avg_pass >= 85)
          score += 15;
      else if (avg_pass < 85 && avg_pass >= 80)
          score += 10;
      else if (avg_pass < 80 && avg_pass >= 75)
          score += 5;
      alert("score :" + score);
    count_avg = count_avg+1;
    let avg = document.getElementById("avg"+count_avg);
    total_avg = total_avg + avg.value;
    avg_avg = total_avg /count_avg;
    if (avg_avg >= 75) {
      score_avg += 25;
    }
    else if (avg_avg < 75 && avg_avg >= 72)
      score_avg += 20;
    else if (avg_avg < 72 && avg_avg >= 69)
      score_avg += 15;
    else if (avg_avg < 69 && avg_avg >= 66)
      score_avg += 10;
    else if (avg_avg < 60 && avg_avg >= 63)
      score_avg += 5;
    alert("Final score " +(score+score_avg));
  }
render() {
      return(
        <div id = "root" className ="container border border-2 p-2 mt-5" > 
            <div  className="row mb-3">    
                    <div className="justify-content-end p-3 d-flex">
                    <h3 className="p-1">Academic Score(Max : 50)</h3>
                    <div className="border rounded p-2" style={{ width: '105px', height: "40px" }}>
                        <p id="Score">
                            0
                        </p>
                    </div>
                </div> </div>
                <div  className="input-group">
                      <label  className="input-group-text" >Academic Qualification </label>
                    </div>
                    
                  <div class="row">
                    <div class="col-sm-2">
                       <input class="form-check-input" type="checkbox" id="Ph.D" value="Ph.D"/>
                       <label class="form-check-label" >Ph.D</label>  
                    </div>
                    <div class="col-sm-4">
                        <input class="form-check-input" type="checkbox" id="PG"  value="PG"/>
                        <label class="form-check-label">M.Sc / M.Com / MBA / MCA / MA</label>
                    </div>

                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="NET"  value="NET"/>
                        <label class="form-check-label">NET</label>
                    </div>
                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="SLET" name="severity[]" value="SLET"/>
                        <label class="form-check-label">SLET</label>
                    </div>
            </div>
            <div className="mt-2">
               <h4>Academic Achievement</h4>
            </div>
                <div className="row  ">
                    <div className="col-10  p-3">
                        <table className="table table-bordered  mt-4 table-striped" >
                            <thead >
                              <tr>
                                <th scope="col">S.no</th>
                                <th scope="col">Name of Subject</th>
                                <th scope="col">Class</th>
                                <th scope="col">pass %</th>
                                <th scope="col">Average %</th>
                                </tr>
                                    </thead>
                                      <tbody id="tablecol">
                                        <tr>
                                          <td scope="row"><input type="text" class="form-control" ></input></td>
                                          <td><input type="text" class="form-control" ></input></td>
                                          <td><input type="text" class="form-control" ></input></td>
                                          <td><input id = "pass" type="text" class="form-control" onKeyPress={this.getpass}></input></td>
                                          <td><input id = "avg" type="text" class="form-control" onKeyPress={this.getavg}></input></td>
                                        </tr>
                                      </tbody>
                                    </table>
                                  <div className="text-end">
                                        <button type="button" class="btn btn-primary" onClick={this.getScore}>Score</button>
                                    </div>
                                </div>
                                <div className="col-2 mt-5">
                                    <button type="button" className="btn btn-sm btn-primary" onClick={this.addRow}>Add more</button>
                                </div>
                            </div>
                        </div>                
      )
    };
}
