import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import '@fortawesome/fontawesome-free/css/all.min.css';
import axios from 'axios';
import React from 'react';
import ListBox from 'react-listbox';
var Inst_name = "";
export class Role_assign extends React.Component {
    state = {
        setinstitution : [],
        setmembers : []
    }
    fillInstitution(){
        console.log("Role assignment");
        axios.post("http://localhost:3001/forInstitution").then((response)=>{
          console.log(response.data);
          this.setState({setinstitution:response.data});
          this.fillmembers();
        })
    };
    fillmembers(){
        axios.post("http://localhost:3001/getmembers",{inst_name : Inst_name}).then((response)=>{
          console.log(response.data);
          this.setState({setmembers:response.data});
        })
    }
    componentDidMount(){
        this.fillInstitution();
    }
    getInstitutionName=(Event)=>{
        console.log(Event.target.value);
        Inst_name = Event.target.value;
    }
    getselectedvalue
    render() {
        return (
            <div className="container">
                <h1> Assigning Roles</h1>
                <div className="row mt-4 w-md-75 mx-auto p-4 shadow justify-content-center">
                    <div className="col-4">
                        <select  name="Insititution Name" id="Insitution_name" className ="form-control form-select"
                              onChange = {this.getInstitution} >
                                {this.setIns_list = this.state.setinstitution.map((ins_list)=>
                                {
                                    return (
                                        <option key = {ins_list.inst_id} value = {ins_list.inst_name}>{ins_list.inst_name}
                                        </option> 
                                        ) 
                                    })}
                            </select> 
                      <div className="list-group">
                          <ListBox onChange={this.getselectedvalue}>
                          { this.mem_list = this.state.setmembers.map((member)=>{
                              return (
                                  <option key = {member.empid} value = {member.empname}>{member.empname}
                                  </option>
                              )
                          })}
                          </ListBox>
                          {/*}  <a href="#" className="list-group-item list-group-item-action">Name</a>
                            <a href="#" className="list-group-item list-group-item-action">Name</a>
                            <a href="#" className="list-group-item list-group-item-action">Name</a>
                            <a href="#" className="list-group-item list-group-item-action">Name</a>
                            <a href="#" className="list-group-item list-group-item-action">Name</a>
                        <a href="#" className="list-group-item list-group-item-action">Name</a> */}
                        </div>
                    </div>
                <div className="col-1 align-self-center  ">
                  <button><i className="fa-solid fa-forward-step " style={{ fontSize: "25px" }}></i></button>  
                    <br />
                    <br />
                   <button><i className="fa-solid fa-forward-fast" style={{ fontSize: "25px"  }}></i></button> 
                    <br />
                    <br />
                    <button><i className="fa-solid fa-backward-step" style={{ fontSize: "25px"}}></i></button>
                    <br />
                    <br />
                    <button><i className="fa-solid fa-backward-fast" style={{ fontSize: "25px"}}></i></button>
                </div>


                    <div className="col-4">
                        <select className="form-select " >
                            <option selected>Level 1</option>
                            <option >Level 2</option>
                            <option >Level 3</option>
                            <option >Level 4</option>
                            <option >Level 5</option>

                        </select> <br />
                        <div className="list-group">

                            <a href="#" className="list-group-item list-group-item-action">Role</a>
                            <a href="#" className="list-group-item list-group-item-action">Role</a>
                            <a href="#" className="list-group-item list-group-item-action">Role</a>
                            <a href="#" className="list-group-item list-group-item-action">Role</a>
                            <a href="#" className="list-group-item list-group-item-action">Role</a>
                            <a href="#" className="list-group-item list-group-item-action">Role</a>

                        </div>
                    </div>
                </div>

            </div>
        )
    }
}
