import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import '@fortawesome/fontawesome-free/css/all.min.css';
var setinstitution = [];
import React from 'react';
export default class Role extends React.Component {
    fillInstitution(){
        axios.post("http://localhost:3001/forInstitution").then((response)=>{
          console.log(response.data);
          setinstitution=response.data;
        })
      };
    render() {
        return (
            <div className="container">
                <div className="row mt-4 w-md-75 mx-auto p-4 shadow justify-content-center">
                    <div className="col-4">
                        <select  name="Insititution Name" id="Insitution_name" className ="form-control form-select"
                              onChange = {this.getInstitution} >
                                {this.setIns_list = this.state.setinstitution.map((ins_list)=>
                                {
                                    return (
                                        <option key = {ins_list.inst_id} value = {ins_list.inst_name}>{ins_list.inst_name}
                                        </option> 
                                        ) 
                                    })}
                      </select><div class="list-group">

                            <a href="#" class="list-group-item list-group-item-action">Name</a>
                            <a href="#" class="list-group-item list-group-item-action">Name</a>
                            <a href="#" class="list-group-item list-group-item-action">Name</a>
                            <a href="#" class="list-group-item list-group-item-action">Name</a>
                            <a href="#" class="list-group-item list-group-item-action">Name</a>
                            <a href="#" class="list-group-item list-group-item-action">Name</a>

                        </div>
                    </div>

                  
                <div className="col-1 align-self-center  ">
                  <button><i class="fa-solid fa-forward-step " style={{ fontSize: "25px" }}></i></button>  
                    <br />
                    <br />
                   <button><i class="fa-solid fa-forward-fast" style={{ fontSize: "25px"  }}></i></button> 
                    <br />
                    <br />
                    <button><i class="fa-solid fa-backward-step" style={{ fontSize: "25px"}}></i></button>
                    <br />
                    <br />
                    <button><i class="fa-solid fa-backward-fast" style={{ fontSize: "25px"}}></i></button>
                </div>


                    <div className="col-4">
                        <select class="form-select " >
                            <option selected>Level 1</option>
                            <option >Level 2</option>
                            <option >Level 3</option>
                            <option >Level 4</option>
                            <option >Level 5</option>

                        </select> <br />
                        <div class="list-group">

                            <a href="#" class="list-group-item list-group-item-action">Role</a>
                            <a href="#" class="list-group-item list-group-item-action">Role</a>
                            <a href="#" class="list-group-item list-group-item-action">Role</a>
                            <a href="#" class="list-group-item list-group-item-action">Role</a>
                            <a href="#" class="list-group-item list-group-item-action">Role</a>
                            <a href="#" class="list-group-item list-group-item-action">Role</a>

                        </div>
                    </div>
                </div>

            </div>
        )
    }
}
