import React, { Component } from 'react'
import PropTypes from 'prop-types'
import axios from 'axios';
import {DataGrid } from '@material-ui/data-grid'
import { rootShouldForwardProp } from '@mui/material/styles/styled';

export  class Academic extends Component {
    state = {
        sub_count :0
    }
    
    get_sub_count(Event){
    }
    addRow(){
        
      var row = document.getElementById('tablecol');
      
        var tr = document.createElement('tr');
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'sub_name');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'class');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'pass');
        td.appendChild(inpt);
        alert(inpt.getAttribute("id"));
        tr.appendChild(td);
        row.appendChild(tr);
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('id', 'avg');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);
  }
    render() {
      return(
        <div id = "root" className ="container border border-2 p-2 mt-5" > 
            <div  className="row mb-3">    
                    <div className="justify-content-end p-3 d-flex">
                    <h3 className="p-1">Academic Score(Max : 50)</h3>
                    <div className="border rounded p-2" style={{ width: '105px', height: "40px" }}>
                        <p id="Score">
                            0
                        </p>
                    </div>
                </div> </div>
                <div  className="input-group">
                      <label  className="input-group-text" >Academic Qualification </label>
                    </div>
                    
                  <div class="row">
                    <div class="col-sm-2">
                       <input class="form-check-input" type="checkbox" id="Ph.D" value="Ph.D"/>
                       <label class="form-check-label" >Ph.D</label>  
                    </div>
                    <div class="col-sm-4">
                        <input class="form-check-input" type="checkbox" id="PG"  value="PG"/>
                        <label class="form-check-label">M.Sc / M.Com / MBA / MCA / MA</label>
                    </div>

                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="NET"  value="NET"/>
                        <label class="form-check-label">NET</label>
                    </div>
                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="SLET" name="severity[]" value="SLET"/>
                        <label class="form-check-label">SLET</label>
                    </div>
            </div>
            <br></br>
            <div className="mt-2">
                                <h4>Student Details</h4>
                            </div>


                            <div className="row  ">
                                <div className="col-10  p-3">
                                    <table className="table table-bordered  mt-4 table-striped" >
                                        <thead >
                                            <tr>
                                                <th scope="col">S.no</th>
                                                <th scope="col">Name of Subject</th>
                                                <th scope="col">Class</th>
                                                <th scope="col">pass %</th>
                                                <th scope="col">Average %</th>
                                                <th scope="col"> Feedback Mark</th>
                                            </tr>
                                        </thead>
                                        <tbody id="tablecol">
                                            <tr>
                                                <td scope="row"><input type="text" class="form-control" ></input></td>
                                                <td><input type="text" class="form-control" ></input></td>
                                                <td><input type="text" class="form-control" ></input></td>
                                                <td><input type="text" class="form-control" ></input></td>
                                                <td><input type="text" class="form-control" ></input></td>
                                                <td><input type="text" class="form-control" ></input></td>
                                            </tr>

                                        </tbody>
                                    </table>

                                    <div className="text-end">
                                        <button type="button" class="btn btn-primary">Save</button>
                                    </div>

                                </div>
                                <div className="col-2 mt-5">
                                    <button type="button" className="btn btn-sm btn-primary" onClick={this.addRow}>Add more</button>
                                </div>
                            </div>
                        </div>

                
      )
    };
}
