import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './style.css';
import Button from '@mui/material/Button';
import React, {location} from 'react';
import axios from 'axios';
import {DataGrid,gridCheckboxSelectionColDef } from '@material-ui/data-grid'
import { Redirect,Link} from 'react-router-dom';
import { Report_upload } from './Report_upload';
import {Addtask} from './add_task'
export class Mrhome extends React.Component {  
  constructor(props) {
    super(props);
    this.state = {
      task_acc : [],
      task_name:""
    }  
   }
  componentDidMount(){
    this.getdata();
  }
  
  getdata=()=>
    {      
        axios.post('http://localhost:3001/total_faculty').then((response)=>{
                this.setState({loading:false,task_acc:response.data});
                   console.log(this.state.task_acc);
      });             
    }
    render() {
      const t_data = this.state.task_acc;        
      console.log(t_data);
      const task_acc_columns = [
        {
          headerName : 'ID',
          field  :'inst_id'
          /*width: 100*/
        },
        {
          headerName : 'Task Name',
          field  :"institution"
          
          /*width: 230,*/
          
        }
              
       ]; 
    return(
        <div style={{ display: 'flex', height: '100%' }}>
            <div style={{ flexGrow: 1 }}>
                <DataGrid id = "task_display" 
                    getRowId={rows => rows.inst_id}
                    rows = {t_data}
                    columns = {task_acc_columns}
                />
            </div>
      </div>
      
    );
  };   
}
