import React, { Component } from 'react'
import PropTypes from 'prop-types'
import axios from 'axios';
import {DataGrid } from '@material-ui/data-grid'
import { rootShouldForwardProp } from '@mui/material/styles/styled';

export  class Academic extends Component {
    state = {
        sub_count :0
    }
    get_sub_count(Event){
    }
    addRow = () => {
      var count_sub = document.getElementById("no_of_sub");
      var no_of_sub = count_sub.value;
      alert(no_of_sub);
      var row = document.getElementById('tablecol');
      for (let j = 0; j< no_of_sub;j++)
      {
        var tr = document.createElement('tr');
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);
        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'sub_name');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'class');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('id', 'pass');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);

        var td = document.createElement('td');
        var inpt = document.createElement('input');
        inpt.setAttribute('class', 'form-control');
        inpt.setAttribute('type', 'text');
        inpt.setAttribute('id', 'avg');
        td.appendChild(inpt);
        tr.appendChild(td);
        row.appendChild(tr);
        
    }
      var total_pass = 0;
      var count =0;
      let score =0;
      var pass = document.getElementById("pass");
      pass.addEventListener("onChange",function(){
        total_pass += total_pass;
        count++;
        avg_pass = total_pass/count;
        if (avg_pass >=95 ){
            score = 25;}
        else if(avg_pass < 95 && avg_pass >=90)
            score = 20;
        else if(avg_pass < 90 && avg_pass >=85)
            score = 15;
        else if(avg_pass < 85 && avg_pass >=80)
            score = 10;
        else if(avg_pass < 80 && avg_pass >=75)
            score = 5;
      })

  }

    render() {
      return(
        <div id = "root" className ="container border border-2 p-2 mt-5" > 
            <div  className="row mb-3">    
                    <div className="justify-content-end p-3 d-flex">
                    <h3 className="p-1">Academic Score(Max : 50)</h3>
                    <div className="border rounded p-2" style={{ width: '105px', height: "40px" }}>
                        <p id="Score">
                            0
                        </p>
                    </div>
                </div> </div>
                <div  className="input-group">
                      <label  className="input-group-text" >Academic Qualification </label>
                    </div>
                    
                  <div class="row">
                    <div class="col-sm-2">
                       <input class="form-check-input" type="checkbox" id="Ph.D" value="Ph.D"/>
                       <label class="form-check-label" >Ph.D</label>  
                    </div>
                    <div class="col-sm-4">
                        <input class="form-check-input" type="checkbox" id="PG"  value="PG"/>
                        <label class="form-check-label">M.Sc / M.Com / MBA / MCA / MA</label>
                    </div>

                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="NET"  value="NET"/>
                        <label class="form-check-label">NET</label>
                    </div>
                    <div class="col-sm-3">
                        <input class="form-check-input" type="checkbox" id="SLET" name="severity[]" value="SLET"/>
                        <label class="form-check-label">SLET</label>
                    </div>
            </div>
            <br></br>
            <div  className="row mb-4">   
                <div class="col-md-8 mb-3"> 
                    <div  className="input-group">
                      <label  className="input-group-text" >Number of Subjects Handled </label>
                    </div>
                </div>
                
                <div class="col-md-7 mb-3"> 
                    <div className="input-group">
                    <input  className="form-control" id = "no_of_sub" type="text" name="Number of Subjects Handled" placeholder="Number of Subjects handled (including Practicals)" />
                    <button className = "form-check-button" type="button" onClick = {this.addRow}> Add Subject details</button>
                    </div>
                </div>
                    <div className="row  ">
                                <div className="col-10  p-3">
                                    <table className="table table-bordered  mt-4 table-striped" >
                                        <thead >
                                            <tr>
                                                <th scope="col">S.No</th>
                                                <th scope="col">Name of Subject</th>
                                                <th scope="col">Class</th>
                                                <th scope="col">pass %</th>
                                                <th scope="col">Average %</th>
                                            </tr>
                                        </thead>
                                        <tbody id="tablecol">
                                            
                                        </tbody>
                                    </table>
                                    </div>
                        </div>              
                  </div>
              </div>
                
      )
    };
}
