import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './style.css';
import React, {location} from 'react';
import axios from 'axios';
import {Pie} from "react-chartjs-2";
var institution = [];
var total_fac = [];
var total_task_assigned = [];
var total_task_completed = [];
var total_task_overdue =[];
const labels = ['Red','Blue','Green']  
const data= {
   datasets:[{
     data:[10,20,30],
     backgroundcolor : ['Red','Blue','Green']  
   }],
   
};
export class Mrhome extends React.Component{
    componentDidMount(){
       this.getdata();
    }
    getdata(){
      axios.post("http://localhost:3001/forInstitution").then((response)=>{
          console.log(response.data);
          institution=response.data
        })    
        axios.post("http://localhost:3001/total_faculty").then((response)=>{
          console.log(response.data);
          total_fac=response.data
        })    
        axios.post("http://localhost:3001/task_assigned").then((response)=>{
          console.log(response.data);
          total_task_assigned=response.data
        })    
        axios.post("http://localhost:3001/task_completed").then((response)=>{
          console.log(response.data);
          total_task_completed=response.data;
        })    
        axios.post("http://localhost:3001/task_overdue").then((response)=>{
          console.log(response.data);
          total_task_overdue = response.data
        })    
    }
    render() {
      const t_data = this.state.task_acc;        
      const task_acc_columns = [
        {
          headerName : 'ID',
          field  :'pr_id'
          /*width: 100*/
        },
        {
          headerName : 'Task Name',
          field  :"task_name",
          resizable:true,
          /*width: 230,*/
          renderCell: (cellValues) => {
            return (
              <Link to = "/SplitTask" params = {{state: cellValues.value}}>{cellValues.value}</Link>
            );
          }
        },
        {
          headerName : 'Assigned By',
          field :'empname',
          
          /*width: 160*/
        },
        {
          headerName : 'Assigned Date',
          field :'ass_date'
          /*width: 150*/
        },
        {
          headerName : 'Closing Date',
          field :'target_date'
          /*width: 150*/
        },
        {
          headerName : 'Status',
          field :'pr_status',
          /*width: 150*/
        },
        {
          headerName : 'Upload Report',
          field  :"ur",
          width: 170,
          renderCell:(cellValues)=>{          
            console.log(this.state.task_acc);
            return (
               <Link to = '/report'>Upload Report</Link>
               )}
        }      
       ];
      return(
        <div style = {{display: 'flex', height: '100%'}}>
            <div style={{ flexGrow: 1 }}>
            <DataGrid id = "task_display" 
            getRowId={rows => rows.pr_id}
            rows = {t_data}
            columns = {task_acc_columns}
      />
              </div>
            </div>
      );
    };
}
