import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './style.css';
import {DataGrid } from '@material-ui/data-grid'
import React from 'react';
import axios from 'axios';
import {Line, Pie, Piechart} from 'react-chartjs-2'
export class Mrhome extends React.Component { 
  state = {
    data : {
      labels: ["Members","Assigned","inprogress","Completed","OverDue"],
      datasets: [{
        label: "KG College of Arts and Science",
        backgroundColor: 'rgb(255, 99, 132)',
        borderColor: 'rgb(255, 99, 132)',
        data: [162,89,56,20,5],
      }]},
    
    inst_report:[]
  }
  componentDidMount(){
    this.getdata();
  }
  getdata=()=>
    {
      axios.post("http://localhost:3001/inst_report").then((response)=>{
        console.log(response.data)
        this.setState({inst_report:response.data});
      }) 
    }
    render() 
    {
      const t_data = this.state.inst_report;        
      console.log(t_data);
      const task_acc_columns = [
        {
          headerName : 'ID',
          field  :'inst_id',
          width: 95
        },
        {
          headerName : 'Institution Name',
          field  :"inst_name",
          width: 250 
        },
        {
            headerName : "Members",
            field:"faculty_count",
            width:150
            
        },
        {
            headerName : "Task Assigned",
            field:"task_assigned",
            width:150
        },
        {
            headerName : "Task Completed",
            field:"task_completed",
            width:150
        },
        {
            headerName : "Task OverDue",
            field:"task_overdue",
            width:150
        }  
      ];
    return(
      <div className = "container">
        <div style={{ display: 'flex', height: '100%' }}>
            <div style={{ flexGrow: 1 }}>
                <DataGrid id = "task_display" 
                    getRowId={rows => rows.inst_id}
                    rows = {t_data}
                    columns = {task_acc_columns}
                  />
            </div>
      </div>
      <div id = "kgcas">
      <Piechart width={700} height={700}>
          <Pie data={data} dataKey="Task" outerRadius={250} fill="green" />
      </Piechart>
      </div>
      </div>
    )
  }
}
